#!/bin/sh
if [ ! -f makefile ]
then
  echo "*** mkconfig.sh: can not find 'makefile' (run './configure.sh' first)"
  exit 1
fi
echo "// Automatically generated by './mkconfig.sh': do not edit!"
echo "#define CLEANELING_OS \"`uname -srmn`\""
echo "#define CLEANELING_COMPILED \"`date`\""
cat<<EOF
#define CLEANELING_RELEASED "Fri Feb  7 07:29:22 CET 2014"
#define CLEANELING_VERSION "00v"
#define CLEANELING_ID "98a0fd17644c17ddb518f5ec21ff98e3f2bbc25f"
EOF
